static lex_toktype _entry(struct rlex* lex, int begin) {
	if (lex->pmax >= lex->size)
		return  Eof ;
	int c;
	int i = lex->pmax;
	int state = begin;
	int prev = begin;
	while(i < lex->size) {
		c = LEX_CHAR(i++);
		state = LEX_TRANS(state, c);
		if (state >= 10)
			break;
		prev = state;
	}
	lex->pmin = i; // if UnMatached then pmin >= pmax
	if (state == 255) {
		state = prev;
		i--;
	}
	int q = LEX_EXIT(state);
	if (i > lex->pmax && q < 16) {
		lex->pmin = lex->pmax;
		lex->pmax = i;
	} else {
		q = LEX_EXIT(begin);
	}
	return _cases(lex, q);
}
static lex_toktype __token(struct rlex* lex) {
	return _entry(lex, 0);
}

// public function
void test_init(struct rlex* lex, lex_srctype *input, int size) {
	lex->pmin = lex->pmax = 0;
	lex->size = size;
	lex->src = (unsigned char*)input;
	lex->token = (int(*)(struct rlex*))__token;
}


int main(int argc, char** argv) {
	char buff[256];
	char* text = "1 + 2 - \"string\" * _ident_ / 101";
	struct rlex lex;

	test_init(&lex, text, strlen(text)); // filename_init

	while(1) {
		int tok = rlex_token(&lex);
		switch(tok) {
		case Eof:
			goto Endloop;
		case CInt:
		{
			long n = atol(rlex_current(&lex));
			printf("%ld\n", n);
		}
			break;
		case CIdent:
		{
			int size = rlex_cursize(&lex);
			memcpy(buff, rlex_current(&lex), size);
			buff[size] = 0;
			printf("%s\n", buff);
		}
			break;
		case CString:
		case CQString:
		{
			int size = rlex_cursize(&lex);
			memcpy(buff, rlex_current(&lex), size);
			buff[size] = 0;
			printf("%s\n", buff);
		}
			break;
		case OpMul:
			printf("*\n");
			break;
		case OpDiv:
			printf("/\n");
			break;
		case OpAdd:
			printf("+\n");
			break;
		case OpSub:
			printf("-\n");
			break;
		default:
			printf("something is wroing, tok: %d\n", tok);
		}
	}
	Endloop:
	return 0;
}
